import React from 'react';
import { connect } from 'react-redux';

import { fetch_default } from '../actions/';

class VideoDetail extends React.Component {

    componentDidMount = () => {
        this.props.fetch_default();
    };

    render() {
        if (!this.props.selected) {
            return <div>Loading...</div>
        };
        const videoSrc = '';
        if (this.props.selected) videoSrc = `https://www.youtube.com/embed/${this.props.selected.id.videoId}`;
        return (
            <div>
                <div className="ui embed">
                    <iframe title="Video Player" src={videoSrc} />
                </div>
                <div className="ui segment">
                    <h4 className="ui header">Title</h4>
                    <p>description</p>
                </div>
            </div>
        );
    };
};

const mapStateToProps = state => {
    return {
        selected: state.selected,
        videos: state.videos
    }
};

export default connect(mapStateToProps, {fetch_default})(VideoDetail);
