import React from 'react';

class NavMenu extends React.Component {
    state = {
        selected: "Home"
    };

    render() {
        const classSelected = this.state.selected;
        return (
            <div className="ui container">
                <div className="ui inverted segment">
                    <div className="ui inverted secondary pointing menu">
                        <p onClick={() => this.setState({selected:"Home"})} className={`${classSelected} item`}>Home</p>
                        <p onClick={() => this.setState({Selected: "Previous"})} className={`${classSelected} item`}>Previous</p>
                    </div>
                </div>
            </div>
        );
    }
}

export default NavMenu;
